function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

var reservedFrom = g_form.getValue('work_start'); 
var reservedTo = g_form.getValue('work_end'); 
var browserDate = new Date();
	alert(browserDate);
var format = g_user_date_time_format;
var reservedFromConverted = getDateFromFormat(reservedFrom, format);
var reservedToConverted = getDateFromFormat(reservedTo, format);
var browserDateConverted = getDateFromFormat(browserDate, format);
	alert(browserDateConverted);
var dateObj = new Date(reservedFrom); 
var reservedFromDay = dateObj.getDay(); // returns 6 for saturday and 0 for Sunday 


if(reservedFromDay == 0 || reservedFromDay == 6) {
	g_form.clearValue('work_start');
	g_form.showFieldMsg('work_start', "You can only make a reservation for a week day");
}
//else if(reservedFromConverted > reservedToConverted /*|| reservedFromConverted > browserDateConverted*/) {
// 	g_form.clearValue('work_start');
// 	g_form.showFieldMsg('work_start', "Dates in the past as well as after the reserved to are not allowed. Please select a future date and remember that it needs to be before reserved to");
// }

}
